mapper.select:
select user_id, field_trip_id, category_name, field_trip_name, field_trip_registation_date, field_trip_deadline_date, field_trip_program_date, field_trip_place, field_trip_price, field_trip_context_description, recommemd_count, field_trip_filePath, field_trip_system_name, field_trip_org_name from view_field_tripdto where (select field_trip_id from kids.view_user_payment where user_id = #{userId})
or category_name = #{categoryName}
or field_trip_program_date = #{fieldTripProgramDate}
or field_trip_name like concat('%', #{fieldTripName}, '%')
order by field_trip_id desc
